  public static String part1(String str) {
    char add = '(';
    char minus = ')';
    int floor = 0;
    for (int i = 0; i < str.length(); i++) {
      if (str.charAt(i) == add) {
        floor++;
      } //end add
      if (str.charAt(i) == minus) {
        floor--;
      } //end minus
    } //end loop
    return "PART1: Floor = " + floor;
  } //end part1()

  public static String part2(String str) {
    char add = '(';
    char minus = ')';
    int floor = 0;
    int position = 0;
    for (int i = 0; i < str.length(); i++) {
      if (str.charAt(i) == add) {
        floor++;
      } //end add
      if (str.charAt(i) == minus) {
        floor--;
      } //end minus
      if (floor == -1) {
        position = i + 1;
        break;
      } //end -1
    } //end loop
    return "PART2: position = " + position;
  } //end part2()

